import React from 'react';
import './Alert.scss';

const Alert = (props) => {

    const onClcikHandler = () => {
        props.onClose()
    }
    function backdropHandler(e){
        if(e.target.classList.contains('backdrop')){
            console.log(e.target.children[0].className);
            e.target.children[0].style.display = 'none';
            e.target.classList.remove('backdrop');
        }
    }
    return (
        <div className="backdrop" onClick={(e)=> backdropHandler(e)}>
                <div className='alert'>
                <div className="alert-child">
                <p>{props.message}</p>
                <button className='btn' onClick={onClcikHandler}>Close</button>
                </div>
             </div>
        </div>

    );
};

export default Alert;
