import React from 'react';
import { useState } from 'react';
import './UserForm.scss'
import UserList from './UserList';
import Alert from '../UI/Alert';

const UserForm = (props) => {
    const [name, setName] = useState('');
    const  [age, setAge] = useState('');
    const [alert, setAlert] = useState(false);
    const [message, setMessage] = useState('Added successfully')
    const onSubmitHandler = (e) => {
        e.preventDefault();
        console.log('cloic')
        setName(name);
        setAge(age);
            if(name !== '' && age !==''){
                const input = {
                    name: name,
                    age : age
                }
                setAlert(false);
               props.onAdd(input);    
            }

       else{
           console.log('show alert')
           setAlert(true);
           setMessage('Please enter both fields')
       }
    }

    return (
        <div className='userform'>
            <form className='form-controls' onSubmit={onSubmitHandler}>
                <div className=''>
                    <label htmlFor='name'>Name</label>
                    <input type='text' id='name' onChange={(e)=> setName(e.target.value)} value={name}/>
                </div>
                <div>
                    <label htmlFor='age'>Age</label>
                    <input type='number' id='age' onChange={(e)=> setAge(e.target.value)} value={age}/>
                </div>
                <button className='btn btn-add'>Add User</button>
            </form>

            {alert ? <Alert/> : ''}
        </div>
    );
};

export default UserForm;
