import React from 'react';
import './Alert.scss';

const Alert = (props) => {

    const onClcikHandler = () => {
        props.onClose()
    }
    return (
        <div className='alert'>
            <div className="alert-child">
            <p>{props.message}</p>
            <button className='btn' onClick={onClcikHandler}>Close</button>
            </div>
        </div>
    );
};

export default Alert;
