import React from 'react';
import { useState } from 'react';
import './UserForm.scss'
import UserList from './UserList';
import Alert from '../UI/Alert';

const UserForm = (props) => {
    const [name, setName] = useState('');
    const  [age, setAge] = useState('');
    let inputs;
    const onSubmitHandler = (e) => {
        e.preventDefault();
        inputs= {
            name: name,
            age : age
        }
            if(inputs.name !== '' && inputs.age !==''){
                setName(name);
                setAge(age);

                props.onAddInput(inputs)
                console.log('on condtion',name, age);
            }

       else{
           return <Alert/>
       }
    }

    return (
        <div className='userform'>
            <form className='form-controls' onSubmit={onSubmitHandler}>
                <div className=''>
                    <label htmlFor='name'>Name</label>
                    <input type='text' id='name' onChange={(e)=> setName(e.target.value)} value={name}/>
                </div>
                <div>
                    <label htmlFor='age'>Age</label>
                    <input type='number' id='age' onChange={(e)=> setAge(e.target.value)} value={age}/>
                </div>
                <button className='btn btn-add'>Add User</button>
            </form>
        </div>
    );
};

export default UserForm;
